import React from "react";
import { Link } from "react-router-dom";
import "./BodyCream.css";
import creamImage from "../Image/exportImages";

import Dialog from "@material-ui/core/Dialog";
import MuiDialogTitle from "@material-ui/core/DialogTitle";
import MuiDialogContent from "@material-ui/core/DialogContent";
import MuiDialogActions from "@material-ui/core/DialogActions";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import Typography from "@material-ui/core/Typography";

class BodyCream extends React.Component {
  state = {
    urls: creamImage,
    open: false,
    setOpen: false
  };
  imageClick = () => {
    this.setState({ isOpen: !this.state.isOpen });
    console.log("cliked");
  };
  renderImage(imageUrl) {
    return (

      <div className="individualImage">
        <img
          src={imageUrl.url}
          // onClick={this.handleClickOpen}
          alt={imageUrl.text}
          width="400"
          height="500"

        />
         {this.state.open && (
          <dialog
            className="dialog"
            style={{ position: "absolute" }}
            open
            onClick={this.handleShowDialog}
          >
          <img
          className="image"
          src={imageUrl.url}
          onClick={this.handleClickOpen}
          alt={imageUrl.text}
        />
      </dialog>
    )}
        <div className="imageText" >{imageUrl.text}  </div>
        <button>ADD TO CART</button>
      </div>
    );
  }
  handleClickOpen = () => {
    console.log("handleClickOpen");
    this.setState({
      setOpen: true
    });
  };
  handleClose = () => {
    this.setState({
      setOpen: false
    });
  };
  render() {
    console.log(this.state.urls);
    return (
      <React.Fragment>
        <div className="linkMain">
          <div className="linkMainFirst">
            <Link to="/">HOME</Link>
            <Link to="/BodyCream">BODY CREAM</Link>
            <Link to="/ShowerGel">SHOWER GEL</Link>
            <Link to="/BodyMists">BODY MISTS</Link>
          </div>
          <div className="pageTitle">
            <h1>
              <span className="pageTitleBodyCream">Select Body Cream</span>
            </h1>
            <span className="pageTitleBodyCreamNumber">21 Items</span>
          </div>
          <div className="imagesMain">
            <div className="imagesMainSecond">
              {this.state.urls.map(img => this.renderImage(img)) }
            </div>
          </div>
          <div>
            {/* <Dialog
              onClose={this.handleClose}
              aria-labelledby="customized-dialog-title"
              open={this.state.open}
            > */}
              Modal title
              <Typography gutterBottom>
                Aenean lacinia bibendum nulla sed consectetur. Praesent commodo
                cursus magna, vel scelerisque nisl consectetur et. Donec sed
                odio dui. Donec ullamcorper nulla non metus auctor fringilla.
              </Typography>

          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default BodyCream;
