import React from "react";
import { Link } from "react-router-dom";
import "./BodyCream.css";
import creamImage from "../Image/exportImages";
import Modal from "react-awesome-modal";
import Button from "@material-ui/core/Button";
import { withStyles } from "@material-ui/core/styles";
import CloseIcon from "@material-ui/icons/Close";

const useStyles = theme => ({
  root: {
    marginLeft: 250,
    marginRight: 10
  },
  button: {
    color: "white",
    backgroundColor: "#333",
    height: "40px",
    width: "120px",
    fontWeight: "400",
    fontSize: "12.5px",
    cursor: "pointer",
    marginTop: "10px",
    letterSpacing: "0.1em",
    borderRadius: "0",
    "&:hover": {
      color: "#333",
      backgroundColor: "white"
    }
  },
  Box: {
    display: "flex",
    flexDirection: "row"
  },
  mainImageText: {
    display: "flex",
    flexDirection: "column",
    paddingTop: "70px"
  },

  mainBoxImage: {
    marginLeft: "0px",
    left: "auto",
    flexDirection: "row",
    color: "#333",
    position: "relative",
    textAlign: "center",
    display: "table-cell",
    verticalAlign: "top",
    lineHeight: "1.2"
  },
  mainImageTextBox: {
    border: "1px solid black",
    margin: "0 0 4px",
    border: "none",
    fontSize: "25px",
    color: "#666666",
    paddingBottom: "0px",
    fontFamily: " Trade Gothic W01 Light,Arial,sans-serif",
    lineHeight: "28px",
    margin: "1.25rem 0",
    fontWeight: " 500",
    position: "relative",
    letterSpacing: "0.25px"
  },
  individualImageTop:{
    // border:"1px solid pink",
    borderBottom: "1px solid #e5e5e5",
    position: "relative",
    padding: "1rem",
    boxSizing:" border-box",
    fontSize: "22px",
 },
 individualImageTopClose:{
  float:"right",
  width:"100px",
  border:"1px solid red",
 },
 individualImageTopText: {
  fontFamily: "BentonSansLight,Helvetica,Arial,sans-serif",
    webkitFontFeatureSettings: "liga 0",
    fontFeatureSettings: "liga 0",
    fontSize: "22px",
    lineHeight: "27px",
    letterSpacing: "0px",
    textTransform: "inherit",
    fontWeight: "400",
  border:"1px solid green",
 },
 individualImageTopHeading:{
  border:"1px solid green",
 }

});

class BodyCream extends React.Component {
  state = {
    urls: creamImage,
    selectedImage: {}
  };

  openModal(imageUrl) {
    this.setState({
      visible: true,
      selectedImage: imageUrl
    });
  }
  closeModal() {
    this.setState({
      visible: false,
      selectedImage: {}
    });
  }

  renderImage(imageUrl) {
    const { classes } = this.props;

    return (
      <div className="individualImage">
        <img
          className={classes.individualImage}
          src={imageUrl.url}
          onClick={() => this.openModal(imageUrl)}
          alt={imageUrl.text}
          width="400"
          height="500"
        />
        <div className="imageText">{imageUrl.text} </div>
        <div className="imageTextPrice">{imageUrl.textPrice} </div>
        <Button variant="contained" className={classes.button}>
          ADD TO BAG
        </Button>
      </div>
    );
  }

  render() {
    console.log(this.state.urls);
    const { classes } = this.props;
    return (
      <React.Fragment>
        <>
          <div className="linkMain">
            <div className="linkMainFirst">
              <Link to="/">HOME</Link>
              <Link to="/BodyCream">BODY CREAM</Link>
              <Link to="/ShowerGel">SHOWER GEL</Link>
              <Link to="/BodyMists">BODY MISTS</Link>
            </div>
            <div className="pageTitle">
              <h1>
                <span className="pageTitleBodyCream">Select Body Cream</span>
              </h1>
              <span className="pageTitleBodyCreamNumber">21 Items</span>
            </div>
            <div className="imagesMain">
              <div className="imagesMainSecond">
                {this.state.urls.map(img => this.renderImage(img))}
              </div>
            </div>
          </div>
          <Modal
            visible={this.state.visible}
            width="700"
            height="600"
            effect="fadeInUp"
            onClickAway={() => this.closeModal()}
          >

              <div className={classes.individualImageTop}>
                <div clasName={classes.individualImageTopText}>
                 <div className={classes.individualImageTopHeading}> Quick View </div>
                <div className={classes.individualImageTopClose}>
                  <a href="/BodyCream" onClick={() => this.closeModal()}>
                    <CloseIcon />
                  </a>
                  </div>
                  </div>
              </div>
              <div className={classes.Box}>
              <div className="mainBox">
                <img
                  className={classes.mainBoxImage}
                  src={this.state.selectedImage.url}
                  alt={this.state.selectedImage.text}
                  width="400"
                  height="500"
                />
              </div>
              <div className={classes.mainImageText}>
                <h1 className={classes.mainImageTextBox}>
                  {this.state.selectedImage.text}{" "}
                </h1>
                <div className={classes.mainImageTextPriceBox}>
                  {this.state.selectedImage.textPrice}{" "}
                </div>
                <Button variant="contained" className={classes.button}>
                  ADD TO BAG
                </Button>
              </div>
            </div>
          </Modal>
        </>
      </React.Fragment>
    );
  }
}

export default withStyles(useStyles)(BodyCream);
